ALICE="/home/elrond/Downloads/devnetWalletKey.pem"
ADDRESS=$(erdpy data load --key=address-testnet-blockHeaderSync)
DEPLOY_TRANSACTION=$(erdpy data load --key=deployTransaction-testnet-blockHeaderSync)
PROXY=https://devnet-gateway.elrond.com
CHAIN_ID=D
PROJECT_HARDCODED="/home/elrond/sc-polynetwork-bridge-rs/BlockHeaderSync"

deploy() {
    erdpy --verbose contract deploy --project=${PROJECT_HARDCODED} --recall-nonce --pem=${ALICE} \
    --gas-limit=200000000 --send --outfile="deploy-testnet.interaction.json" \
    --proxy=${PROXY} --chain=${CHAIN_ID} || return

    TRANSACTION=$(erdpy data parse --file="deploy-testnet.interaction.json" --expression="data['emitted_tx']['hash']")
    ADDRESS=$(erdpy data parse --file="deploy-testnet.interaction.json" --expression="data['emitted_tx']['address']")

    erdpy data store --key=address-testnet-blockHeaderSync --value=${ADDRESS}
    erdpy data store --key=deployTransaction-testnet-blockHeaderSync --value=${TRANSACTION}

    echo ""
    echo "Smart contract address: ${ADDRESS}"
}

syncGenesisHeader() {
    erdpy --verbose contract call ${ADDRESS} --nonce=${alice_nonce} --pem=${ALICE} --gas-limit=100000000 --function="syncGenesisHeader" --arguments  --send --proxy=${PROXY} --chain=${CHAIN_ID}
}

syncBlockHeader() {
    erdpy --verbose contract call ${ADDRESS} --nonce=${alice_nonce} --pem=${ALICE} --gas-limit=10000000 --function="syncBlockHeader" --arguments  --send --proxy=${PROXY} --chain=${CHAIN_ID}
}

verifyHeader() {
    erdpy --verbose contract call ${ADDRESS} --nonce=${alice_nonce} --pem=${ALICE} --gas-limit=10000000 --function="verifyHeader" --arguments  --send --proxy=${PROXY} --chain=${CHAIN_ID}
}

getHeaderByHeight() {
    erdpy --verbose contract query ${ADDRESS} --function="getHeaderByHeight" --arguments 0x0000000000000000 0x00000000 --proxy=${PROXY}
}
